-- Table contenant la liste des services proposés par le garage
DROP TABLE IF EXISTS services;
CREATE TABLE services (
  id INT NOT NULL AUTO_INCREMENT,
  name VARCHAR(255) NOT NULL,
  amount DECIMAL(10, 2),
  description TEXT NOT NULL,
  image TEXT NOT NULL,
  PRIMARY KEY (id)
);
INSERT INTO services (name, amount, description, image) VALUES ("ENTRETIEN ET VIDANGE", 79.90, "Il est important de faire une révision complète et la vidange de sa voiture avant un long trajet. Si vous partez en vacances, prenez rendez-vous dans notre garage afin de vous en occuper.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("COURROIE DE DISTRIBUTION", 235.00, "La courroie de distribution est essentielle pour le moteur. Sans elle, celui-ci se détruit sous le coup de sa propre action. Rendez-vous dans notre garage pour la tester avant qu'elle ne cède. Faites votre devis en ligne.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("RÉVISION - RECHARGE CLIMATISATION", 64.90, "La climatisation peut faire une énorme différence lors d’un trajet en voiture. C’est pourquoi il est important de s’occuper de l’entretien de sa climatisation.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("FREINAGE - DISQUES ET/OU PLAQUETTES", 40.00, "Les freins sont le seul rempart entre la vitesse de la voiture et votre sécurité. C'est pourquoi s'ils ne répondent pas bien, vous devriez les faire vérifier dans l'un de nos garages AD avant de courir un risque.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("SUSPENSIONS - AMORTISSEURS", 140.00, "Évitez d’abîmer votre voiture à chaque secousse ! Grâce aux amortisseurs, vous bénéficiez d'un meilleur confort et vos essieux subissent moins les cahots de la route. Bénéficiez du savoir faire des garages AD.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("EMBRAYAGE - KIT COMPLET", 349.00, "L'embrayage est ce qui permet de changer le rapport de conversion de la puissance du moteur en vitesse. Il est donc essentiel qu'il soit en bon état. Chez AD, nous nous occupons de votre embrayage efficacement.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("BOITIER BIO ÉTHANOL", 806.00, "Le boîtier bioéthanol, aussi appelé boîtier flexfuel ou boîtier E85, est un boîtier électronique injectant du bioéthanol E85 dans le carburant, réduisant ainsi les émissions de microparticules et de CO2 de votre véhicule", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("PURGE LIQUIDE DE REFROIDISSEMENT", 15.00, "Votre moteur à un coup de chaud ? Profitez-en, prenez un rendez-vous.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("DIAGNOSTIC ELECTRONIQUE", 18.00, "Les systèmes électroniques de la voiture interviennent pour beaucoup dans votre sécurité. Chez AD, nous nous occupons du diagnostic électronique de votre voiture. Faites confiance à nos experts multimarques.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("PURGE LIQUIDE DE FREIN", 16.00, "Votre voiture a besoin pour fonctionner de différents liquides : niveau d'huile, liquide de refroidissement, liquide de freinage… AD vérifie les niveaux de chacun de ses liquides et les remplace si besoin.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("DÉMARRAGE ET CHARGE - BATTERIE", 79.00, "La batterie, l'alternateur et le démarreur de votre voiture fonctionnent de concert. Sans courant électrique, pas de système électronique et pas de démarrage de l'automobile. AD prend soin de votre batterie.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("VANNE EGR", 330.00, "La vanne EGR est obligatoire sur tous les véhicules à moteur diesel afin de réduire les émissions de gaz toxique. Assurez-vous que cette dernière soit en bon état dans notre garage avant l'amende. AD vous propose des garages compétents pour le changement de votre vanne EGR.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("PRÉ-CONTRÔLE TECHNIQUE", 20.00, "Votre contrôle technique approche ! Nos experts en mécanique pourront préparer votre voiture. Anticipez les problèmes et présentez-vous à votre contrôle technique l’esprit serein ! Obtenez un RDV dès maintenant sur notre site.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("ÉCHAPPEMENT - SILENCIEUX ARRIÈRE", 103.00, "Votre échappement est ce qui permet au CO2 généré par votre voiture de ne pas envahir votre habitacle. Son entretien et la propreté de son conduit sont donc vitaux.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("DIAGNOSTIC SÉCURITÉ", 20.00, "Votre sécurité n'a pas de prix ! Pensez à réaliser un diagnostic chez les réparateurs AD formés et équipés d'outils de diagnostic multimarques. Obtenez dès maintenant un devis et un RDV pour le diagnostic de votre voiture.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("GÉOMÉTRIE ET PARALLÉLISME", 30.00, "L'entretien de ses roues ne consiste pas simplement à s'assurer de leur bon état : il faut aussi vérifier leur parallélisme. Chez AD, nous faisons cette vérification pour vous afin de réduire vos frais d'entretien.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("RÉPARATION PLASTIQUE AUTO", 48.00, "Vos coques de rétroviseurs de voiture sont abîmées ou rayées. Les garages AD vous proposent la rénovation d’une ou deux coques de rétroviseur. Votre voiture paraîtra intacte comme au premier jour.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("RÉNOVATION CARROSSERIE", 159.00, "Vous souhaitez donner un coup de jeune à votre véhicule ? Vous aimeriez effacer les traces de coups apparues au fil des mois et des années ? Passez chez un réparateur AD pour sa rénovation.", "data:image/avif;base64,");
INSERT INTO services (name, amount, description, image) VALUES ("PHARES - RÉNOVATION OPTIQUE", 30.00, "Occupez-vous de vos phares afin de gagner en visibilité la nuit. AD s'occupe de la réparation et de l'entretien de vos phares. Réalisez votre devis en ligne.", "data:image/avif;base64,");


