-- Table contenant la liste des commentaires
DROP TABLE IF EXISTS comments;
CREATE TABLE comments (
  id INT NOT NULL AUTO_INCREMENT,
  approved TINYINT(1) DEFAULT 0,
  name VARCHAR(255),
  comment TEXT,
  rating DECIMAL(10, 1),
  dt DATETIME DEFAULT NOW(),
  ip VARCHAR(15),
  PRIMARY KEY (id)
);


